# Merge the Tools!
# https://www.hackerrank.com/challenges/merge-the-tools

# Sample Input:
# AABCAAADA
# 3   
#
# Sample Output:
# AB
# CA
# AD

def merge_the_tools(string, k):
   # your code goes here
   for i in range(0, len(string), k):
      kstring = ''
      for j in range(k):
         if string[i+j] not in kstring:
            kstring = kstring + string[i+j]
      print(kstring)

if __name__ == '__main__':
#    string, k = input(), int(input())
   string = 'AABCAAADA'
   k = 3
   merge_the_tools(string, k) # AB CA AD

   string = ''
   k = 3452
   merge_the_tools(string, k) # KYQTWXDLINFBHRGZVCUSAMOEPJ YUGTZIWNVSALBXOCDMPFEKJRQH
